-- =======================================================
-- 1. MODELAGEM FÍSICA (Criação do Banco e Tabelas)
-- =======================================================

-- Criar e selecionar o banco de dados
DROP DATABASE IF EXISTS clinica_veterinaria;
CREATE DATABASE clinica_veterinaria;
USE clinica_veterinaria;

-- Tabela CLIENTE
CREATE TABLE CLIENTE (
    id_cliente INT AUTO_INCREMENT PRIMARY KEY,
    nome VARCHAR(30)
);

-- Tabela ENDERECO (1:N com Cliente)
CREATE TABLE ENDERECO (
    id_endereco INT AUTO_INCREMENT PRIMARY KEY,
    rua VARCHAR(10),
    bairro VARCHAR(10),
    cidade VARCHAR(10),
    id_cliente INT,
    FOREIGN KEY (id_cliente) REFERENCES CLIENTE(id_cliente)
);

-- Tabela TELEFONES (1:N com Cliente)
CREATE TABLE TELEFONES (
    id_telefone INT AUTO_INCREMENT PRIMARY KEY,
    fixo VARCHAR(10),
    celular VARCHAR(10),
    id_cliente INT,
    FOREIGN KEY (id_cliente) REFERENCES CLIENTE(id_cliente)
);

-- Tabela ANIMAL (1:N com Cliente)
CREATE TABLE ANIMAL (
    id_animal INT AUTO_INCREMENT PRIMARY KEY,
    nome VARCHAR(10),
    data_nasc DATE,
    id_cliente INT,
    FOREIGN KEY (id_cliente) REFERENCES CLIENTE(id_cliente)
);

-- Tabela PRONTUARIO (1:1 com Animal)
CREATE TABLE PRONTUARIO (
    id_prontuario INT AUTO_INCREMENT PRIMARY KEY,
    hist_alergias VARCHAR(100),
    observacoes VARCHAR(100),
    id_animal INT UNIQUE,
    FOREIGN KEY (id_animal) REFERENCES ANIMAL(id_animal)
);

-- Tabela CONSULTA (1:N com Animal)
CREATE TABLE CONSULTA (
    id_consulta INT AUTO_INCREMENT PRIMARY KEY,
    data_hora DATETIME,
    nm_veterinario VARCHAR(30),
    diagnostico VARCHAR(100),
    id_animal INT,
    FOREIGN KEY (id_animal) REFERENCES ANIMAL(id_animal)
);

-- Tabela MEDICAMENTO (1:N com Consulta - neste modelo físico)
CREATE TABLE MEDICAMENTO (
    id_med INT AUTO_INCREMENT PRIMARY KEY,
    nm_comercial VARCHAR(50),
    laboratorio VARCHAR(50),
    id_consulta INT,
    FOREIGN KEY (id_consulta) REFERENCES CONSULTA(id_consulta)
);


