-- 1. Relatório Geral de Clientes e seus Animais
SELECT c.nome AS Dono, a.nome AS Pet, a.data_nasc AS Nascimento
FROM CLIENTE c
JOIN ANIMAL a ON c.id_cliente = a.id_cliente
ORDER BY c.nome ASC;

-- 2. Contatos de Clientes de uma Cidade Específica (SP)
SELECT c.nome, t.celular, e.cidade, e.bairro
FROM CLIENTE c
JOIN ENDERECO e ON c.id_cliente = e.id_cliente
JOIN TELEFONES t ON c.id_cliente = t.id_cliente
WHERE e.cidade LIKE '%SP%'
ORDER BY c.nome;

-- 3. Animais com Alergias Registradas
SELECT a.nome AS Animal, c.nome AS Dono, p.hist_alergias AS Alergia
FROM ANIMAL a
JOIN CLIENTE c ON a.id_cliente = c.id_cliente
JOIN PRONTUARIO p ON a.id_animal = p.id_animal
WHERE p.hist_alergias != 'Nenhuma'
ORDER BY a.nome;

-- 4. Consultas Realizadas pelo "Dr. Silva"
SELECT cons.data_hora, a.nome AS Paciente, cons.nm_veterinario, cons.diagnostico
FROM CONSULTA cons
JOIN ANIMAL a ON cons.id_animal = a.id_animal
WHERE cons.nm_veterinario LIKE '%Silva%'
ORDER BY cons.data_hora DESC;

-- 5. Rastreamento de Medicamentos (Laboratório "BioVet")
SELECT m.nm_comercial AS Remedio, m.laboratorio, a.nome AS Animal, cons.data_hora
FROM MEDICAMENTO m
JOIN CONSULTA cons ON m.id_consulta = cons.id_consulta
JOIN ANIMAL a ON cons.id_animal = a.id_animal
WHERE m.laboratorio = 'BioVet'
ORDER BY cons.data_hora;

