SELECT c.nome AS Cliente, a.nome AS Pet, cons.data_hora, cons.diagnostico
FROM CLIENTE c
JOIN ANIMAL a ON c.id_cliente = a.id_cliente
JOIN CONSULTA cons ON a.id_animal = cons.id_animal
WHERE c.id_cliente = 50;

-- 7. Animais Idosos (Nascidos antes de 2015)
SELECT a.nome, a.data_nasc, c.nome AS Dono
FROM ANIMAL a
JOIN CLIENTE c ON a.id_cliente = c.id_cliente
WHERE YEAR(a.data_nasc) < 2015
ORDER BY a.data_nasc ASC;

-- 8. Diagnósticos Específicos (Otite)
SELECT a.nome AS Animal, cons.diagnostico, m.nm_comercial AS Medicamento
FROM CONSULTA cons
JOIN ANIMAL a ON cons.id_animal = a.id_animal
JOIN MEDICAMENTO m ON cons.id_consulta = m.id_consulta
WHERE cons.diagnostico LIKE '%Otite%'
ORDER BY a.nome;

-- 9. Prontuário com Observações de Risco (Monitorar)
SELECT a.nome, p.observacoes
FROM PRONTUARIO p
JOIN ANIMAL a ON p.id_animal = a.id_animal
WHERE p.observacoes LIKE '%Monitorar%'
ORDER BY a.nome;

-- 10. Relatório Financeiro/Operacional (Total de Consultas por Veterinário)
SELECT nm_veterinario, COUNT(*) AS Total_Consultas
FROM CONSULTA
GROUP BY nm_veterinario
ORDER BY Total_Consultas DESC;